sys_script_email.script:
(function runMailScript( /* GlideRecord */ current, /* TemplatePrinter */ template,/* Optional EmailOutbound */
    email, /* Optional GlideRecord */ email_action,/* Optional GlideRecord */event) 
	{
    var message = 'Hello ' + current.u_requested_for.first_name + ' ' + current.u_requested_for.last_name + ', ' + '<br><br> A new needit request was submitted on your behalf by ' + event.parm1 + 'txt from email script';
    email.setSubject('This subject is set from Email script' + email_action.name);
    email.addAddress('cc', 'joe.employee@something.com');
    template.print(message);

})(current, template, email, email_action, event);
