sys_script_fix.script:
//Incidents recorded for admin users
var gr=new GlideRecord('incident');
gr.addQuery('caller_id.roles','skill_user');
gr.query();
while(gr.next())
{
    gs.info(gr.number+': '+gr.caller_id.name + ': '+gr.caller_id.roles);

	gr.short_description='Updated with fix script';
	gr.update();
}
