function NeeditStatesObject() {
//setting object values for function
    this.states = {
        'Requested': 13,
        'Awaiting_Approval': 14,
        'Approved': 15,
        'Awaiting_Feedback': 16,
        'Closed_Complete': 3,
        'Withdrawn': 5
    };

//creating variable in the function
    var States = {
        'Requested': 13,
        'Awaiting_Approval': 14,
        'Approved': 15,
        'Awaiting_Feedback': 16,
        'Closed_Complete': 3,
        'Withdrawn': 5
    };
    return JSON.stringify(States);
}
//Second function in script include which can be invoked by using gs.include('NeeditFunctions') and using NeeditState();
function NeeditState(state) {
    switch (state) {
        case 'Requested':
            return 13;
        case 'Awaiting_Approval':
            return 14;
        case 'Approved':
            return 15;
        case 'Awaiting_Feedback':
            return 16;
        case 'Closed_Complete':
            return 3;
        case 'Withdrawn':
            return 5;
    }
}
