sys_script_include.script:
gs.include('NeeditStatex');
var NeeditUsers = Class.create();
NeeditUsers.prototype = {
    initialize: function() {},
    getNeeditUsers: function() {
        var gr = new GlideRecord('sys_user_has_role');
        var sq = gr.addJoinQuery('sys_user_role', 'role', 'sys_id');
        sq.addCondition('name', 'CONTAINS', 'x_58872_need');
        gr.query();
        var users = [];
        while (gr.next()) {
            users.push(gr.user.toString());
        }
        return 'sys_idIN' + users;
    },
    getNeeditUsersFilter: function() {
        var gr = new GlideRecord('sys_user_has_role');
        var sq = gr.addJoinQuery('sys_user_role', 'role', 'sys_id');
        sq.addCondition('name', 'CONTAINS', 'x_58872_need');
		gr.query();
        var users = [];
        while (gr.next()) {
            users.push(gr.user.toString());
        }
        return users + '';
    },
    type: 'NeeditUsers'
};
