sys_transform_script.script:
(function runTransformScript(source, map, log, target /*undefined onStart*/ ) {
    target.work_notes = 'Source: \n' + source.u_needit + '\n\n' + 'Target:\n' + target.number;
    if (!(source.u_short_description)) {
        target.short_description = 'since short description is empty it is set in onbefor trans script';
    }
    if (!(source.description)) {
        target.description = 'This data is loaded from Import Sets and default set by onBefore transform event script';
    }
    if (source.u_what_needed == 'Other') {
        //target.assignment_group='Hardware';
        target.u_other = 'Dummy from OnBefor transform event script';
    }
    if ((source.u_needit == target.number) && (source.u_requested_for = target.u_requested_for)) {
        target.work_notes = 'Transformation Ran however no fields to update';
        ignore = true;
        log.info('Transformation Ran however no fields to update');
    }
})(source, map, log, target);
