sys_web_service.script:
(function scriptedWebServiceOperation(request, response) {
    var query = request.query;
    var needgr = new GlideRecord('x_58872_needit_needit');
    needgr.addEncodedQuery(query);
    gs.info('needitlist webservice' + query);
    needgr.query();
    var needs = [];
    while (needgr.next()) {
        var need = {};
        need.number = needgr.getDisplayValue('number');
        need.requestor = needgr.getDisplayValue('u_requested_for');
        needs.push(need);

    }

    var arrayofneeds = '';
    for (var i in needs) {
        arrayofneeds = arrayofneeds + "<need><number>" + needs[i].number + "</number><requestor>" + needs[i].requestor + "</requestor></need>";
    }

    var xmldoc = "<needs>" + arrayofneeds + "</needs>";
    response.needs = xmldoc;

})(request, response);
