sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    var requestbody = request.body.data;
	var pathparams=request.pathParams;
	var headers= request.headers;
	var queryparams=request.queryParams;

    // implement resource here
    var req = {
        'body': requestbody,
		'pathparms':pathparams,
		'queryparams':queryparams,
		'headers':headers


    };
    response.setBody(req);
	//return param;

})(request, response);

function textreader(request, trim) {
        var reqBodyLine;
        var reqBodyString = '';
        var streamBody = request.body.data;
        var gtrRequestBodyReader = new GlideTextReader(streamBody);

        //get first line
        reqBodyLine = gtrRequestBodyReader.readLine();
        while(typeof reqBodyLine == 'string') {
            reqBodyString += reqBodyLine + '\n';

            //get next line
            reqBodyLine = gtrRequestBodyReader.readLine();
        }

        return trim ? reqBodyString.trim() : reqBodyString;
    }
