sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    var body = request.body.data;
    var table = request.queryParams.table;
    var sysid = request.queryParams.sys_id;
    var file_name = request.queryParams.file_name;
    var content = body.file_content;
    var contenttype = body.contenttype;

    try {
        var gr = new GlideRecord(table);
        gr.get(sysid.toString());
        if (gr.isValid()) {
            var gsa = new GlideSysAttachment();
            gsa.writeBase64(gr, file_name, contenttype, content);
            return 'attachment added succesfully';
        }
    } catch (ex) {
        var err = new sn_ws_err.ServiceError();
        err.setDetail(ex.message);
        err.setMessage('Something Wrong!');
        return err;
    }

})(request, response);
