sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    var number = request.pathParams.number;
    var requestbody = request.body.data.detail;
    var JSONobj = requestbody;
    if (!requestbody) {
        return new sn_ws_err.BadRequestError('no body in the request');
    }
    var gr = new GlideRecord('x_58872_needit_needit');
    gr.addQuery('number', number);
    gr.query();
    gr.next();
    if (gr.sys_id) {
        for (var key in JSONobj) {
            gr[key] = JSONobj[key];
        }
        gr.update();
        if (!gr.getLastErrorMessage()) {
            return ('record updated');
        } else {
            var error = new sn_ws_err.ServiceError();
            error.setStatus(450);
            error.setDetail(gr.getLastErrorMessage());
            return error;
        }
    } else {
        return new sn_ws_err.NotFoundError('could not find record');
    }

})(request, response);
