sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

   var jsonbody=request.body.data.detail;
	if(!jsonbody)
		{
			var err= new sn_ws_err.ServiceError();
			err.setDetail('Body for the request is null');
			err.setStatus(404);
			return err;
		}
	//var jsonbody=JSON.parse(body);
	var ngr= new GlideRecord('x_58872_needit_needit');
	gs.info('Needit Create Rest API triggered');
	ngr.initialize();
	ngr.u_requested_for=jsonbody.u_requested_for;
	ngr.u_requested_for_email=ngr.u_requested_for.email;
	ngr.u_request_type=jsonbody.u_request_type;
	ngr.u_what_needed=jsonbody.u_what_needed;
// 			for (var key in jsonbody)
// 			{
// 				ngr.setValue(key,jsonbody[key]);


// 			}
	ngr.insert();
	if(ngr.getLastErrorMessage())
		{
			return ngr.getLastErrorMessage();
		}
	var record=ngr.getValue('number');
	return record;
	//response.setBody(record);

})(request, response);
