sysevent_in_email_action.script:
(function runAction( /*GlideRecord*/ current, /*GlideRecord*/ event, /*EmailWrapper*/ email, /*ScopedEmailLogger*/ logger, /*EmailClassifier*/ classifier) {
    try {
        gs.info('started processing incoming needit mail' + "\n email body: \n " + JSON.stringify(email));
        //         var grobj = {};
        //         for (var key in current) {
        //             grobj[key] = current[key].toString();
        //         }
        //         gs.info('Create New Needit Task: Current glideRecord:\n ' + JSON.stringify(grobj));

        current.u_needit.setDisplayValue(email.body.needit);
        current.short_description = email.subject + ":" + email.body.assignee;
        current.assigned_to.setDisplayValue(email.body.assignee);
        current.assignment_group.setDisplayValue(email.body.assignment_group);
        current.description = 'payload :' + JSON.stringify(email.body);
        logger.log('Read mail and built JSON, now evaluating ACL for user' + gs.getUserName());
        if (current.canCreate()) {
            logger.log('Evaluated ACL');
            current.insert();
        }
        gs.info('finished processing incoming needit mail');
    } catch (ex) {
        gs.info('caught exception :' + ex);
    }
})(current, event, email, logger, classifier);
